function [Fsr,Fsv,fc] = AssembleSurfTractions(ieles,nnps,nps,fes,Fsr,Fsv,fc)%  assemble arrays with surface datafor   i=1:1:nnps	        i3=3*nps(i);	  i2=i3-1;      i1=i3-2;            ii3 =3*i;      ii2 = ii3-1;      ii1 = ii3-2;              Fsr(fc)=i1;         Fsv(fc)=fes(ii1);         fc=fc+1;                Fsr(fc)=i2;         Fsv(fc)=fes(ii2);           fc=fc+1;                Fsr(fc)=i3;         Fsv(fc)=fes(ii3);         fc=fc+1;  end