function shafac_surf_structure = ShapeFunctionArrays_Surface()%generates surface shape functions and shape function derivatives for 6 node triangles on surfaces of 10-node tet        nnps = 6;      nqpts=7;		  a1=.05971587178977;      b1=.47014206410512;      c1=.79742698535309;      d1=.10128650732346;      qpts(1,1)=0.33333333333333;      qpts(1,2)=0.33333333333333;      qpts(2,1)=a1;      qpts(2,2)=b1;      qpts(3,1)=b1;      qpts(3,2)=a1;      qpts(4,1)=b1;      qpts(4,2)=b1;      qpts(5,1)=c1;      qpts(5,2)=d1;      qpts(6,1)=d1;      qpts(6,2)=c1;      qpts(7,1)=d1;      qpts(7,2)=d1;      swts(1)=0.1125;      swts(2)=0.06619707639425;      swts(3)=swts(2);      swts(4)=swts(2);      swts(5)=0.06296959027241;      swts(6)=swts(5);      swts(7)=swts(5);	   for i=1:1:nnps                                                        for  j=1:1:nqpts                                                        q1=qpts(j,1);                                                             q2=qpts(j,2);                                                             ssfac(i,j)=sfn(q1,q2,i);                                                  dnda(i,j)=fxi(q1,q2,i);                                                 dndb(i,j)=fet(q1,q2,i);                                                 swt(j)=swts(j);      end   end  function sf=sfn(q1,q2,n)	    switch n    case 3      sf=(2.0*q1-1.0)*q1;	    case 4      sf=4.0*q2*q1;	    case 5      sf=(2.0*q2-1.0)*q2;	    case 6       sf=4.0*q2*(1.0-q1-q2);		    case 1       sf=(1.0-2.0*q1-2.0*q2)*(1.0-q1-q2);	    case 2       sf=4.0*q1*(1.0-q1-q2);	    end  endfunction fi=fxi(q1,q2,n)    switch n    case 3      fi=4.0*q1-1.0;	    case 4      fi=4.0*q2;	    case 5      fi=0.0;	    case 6      fi=-4.0*q2;		    case 1      fi=-3.0+4.0*q1+4.0*q2;	    case 2      fi=4.0*(1.0-2.0*q1-q2);	    end	  endfunction ft=fet(q1,q2,n) 		      switch n    case 3      ft=0.0;	    case 4      ft=4.0*q1;	    case 5      ft=4.0*q2-1.0;	    case 6      ft=4.0*(1.0-q1-2.0*q2);		    case 1      ft=-3.0+4.0*q1+4.0*q2;	    case 2      ft=-4.0*q1;	    endend shafac_surf_structure    = Data_Structure_Shafac_Surf(nnps,nqpts,swts,ssfac,dnda,dndb);end