function  [n rjs] = SurfJacobian(ieles,nnps,nqpts,dnda,dndb,nps,x,y,z)%  computes surface jacobian termsrjs = zeros(1,nqpts);n=zeros(nqpts,3);for i=1:1:nqpts          tjac = zeros(3,3);           for j=1:1:nnps  	                          tjac(2,1)=tjac(2,1)+dnda(j,i)*x(j);                                tjac(2,2)=tjac(2,2)+dnda(j,i)*y(j);                     tjac(2,3)=tjac(2,3)+dnda(j,i)*z(j); 	        tjac(3,1)=tjac(3,1)+dndb(j,i)*x(j);                                 tjac(3,2)=tjac(3,2)+dndb(j,i)*y(j);                                tjac(3,3)=tjac(3,3)+dndb(j,i)*z(j);  	    end          n1 = tjac(2,2)*tjac(3,3) - tjac(3,2)*tjac(2,3);      n2 = tjac(3,1)*tjac(2,3) - tjac(2,1)*tjac(3,3);      n3 = tjac(2,1)*tjac(3,2) - tjac(3,1)*tjac(2,2);      nvecmag = sqrt(n1*n1+n2*n2+n3*n3);        tjac(1,1)=n1/nvecmag;                                 tjac(1,2)=n2/nvecmag;	        tjac(1,3)=n3/nvecmag;                                                                           rjs(i)=det(tjac);      n(i,1:3)=tjac(1,1:3);      end